  grid-template-rows: auto auto auto;
  align-items: center;
  gap: 0px 10px;
  padding: 8px;
  background: var(--bg-tertiary);
  border-radius: 10px;
  border: 1px solid var(--border-subtle);
  transition: all 0.2s ease;
  position: relative;
  overflow: hidden;
}

.char-item::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 2px;
  background: linear-gradient(90deg, 
    var(--favor-neutral) 0%, 
    transparent 100%);
  opacity: 0.6;
  transition: background 0.3s ease;
}

.char-item:hover {
  background: var(--bg-hover);
  border-color: var(--border-hover);
  transform: translateY(-1px);
}

.char-item[data-content=""] {
  display: none;
}

.char-name-icon {
